<!DOCTYPE html><html><head>	<meta charset="utf-8">	<meta name="viewport" content="width=device-width, initial-scale=1">	<title>Reading from DB</title>	<link href="https://cdnjs.cloudflare.com/ajax/libs/semantic-ui/2.4.1/semantic.min.css" rel="stylesheet"></head><body>	<?php		require_once('db_connect.php');		$connect = mysqli_connect( HOST, USER, PASS, DB )			or die("Can not connect");		$results = mysqli_query( $connect, "SELECT * FROM employees" )			or die("Can not execute query");	?>	<div class="ui menu">		<h1 class="header">Employees Table</h1>		<div class="right menu">			<a href="insert.php" class="item">Add New Entry</a>		</div>	</div>	<div class="ui text container">		<a href="index.php"><button class="ui green button">Go back to homepage</button></a>		<?php			echo "<table class='ui table'> \n";			echo "<thead><th>First Name</th> <th>Job ID</th> <th>Salary</th> <th>Manager</th><th>Options</th> </thead> \n";			while( $rows = mysqli_fetch_array( $results ) ) {				extract( $rows );				echo "<tr>";				echo "<td> $FIRST_NAME </td>";	//The variable name has to match EXACTLY like the db column name				echo "<td> $JOB_ID </td>";				echo "<td> $SALARY </td>";				echo "<td> $MANAGER_ID </td>";				echo "<td> <a style='color: red;' href='delete.php?id=$EMPLOYEE_ID'>Delete</a> </td>";				echo "</tr> \n";			}			echo "</table> \n";		?>		</div></body></html>